import { createStore } from 'vuex'
import axios from 'axios';

const store = createStore ({
    state () {
        return{
            productsNew: [],
            productsSale: []
        }
    },
    mutations:{
        SET_PRODUCTS_NEW_TO_STATE: (state, productsNew) => {
            state.productsNew=productsNew;
        }
    },
    actions: {
        GET_PRODUCTS_NEW_FROM_API({commit})  {
            return axios('http://localhost:3000/productsNew', {
                method: "GET"
            })
                .then((productsNew) => {
                    commit('SET_PRODUCTS_TO_STATE', productsNew.data);
                    return productsNew;
                })
                .catch((error) => {
                    console.log(error)
                    return error
                })
        }
    },
    getters: {
        PRODUCTS_NEW(state) {
            return state.productsNew;
        }
    }
});
export default store;
